define({"topics":[{"title":"Interpersonal functions","shortdesc":"<p class=\"shortdesc\">A linguistic function in which language expresses the relations between speaker and         listener. <\/p>","href":"bilingualism\/codeswitching\/codeswitch_reasons_for_switching.html#codeswitch_interpersonal_functions","attributes":{"data-id":"codeswitch_interpersonal_functions"},"menu":{"hasChildren":false},"tocID":"codeswitch_interpersonal_functions-d883e884","topics":[]},{"title":"Pragmatic functions","shortdesc":"<p class=\"shortdesc\">Pragmatic function is the meaning a speaker wishes to convey to the person they are         speaking to (the addressee).<\/p>","href":"bilingualism\/codeswitching\/codeswitch_reasons_for_switching.html#codeswitch_pragmatic_functions","attributes":{"data-id":"codeswitch_pragmatic_functions"},"menu":{"hasChildren":false},"tocID":"codeswitch_pragmatic_functions-d883e895","topics":[]},{"title":"Rhetorical function","shortdesc":"<p class=\"shortdesc\">The art of persuation.<\/p>","href":"bilingualism\/codeswitching\/codeswitch_reasons_for_switching.html#codeswitch_rhetorical_functions","attributes":{"data-id":"codeswitch_rhetorical_functions"},"menu":{"hasChildren":false},"tocID":"codeswitch_rhetorical_functions-d883e906","topics":[]},{"title":"Stylistic function","shortdesc":"<p class=\"shortdesc\">Stylistic functions are not associated with mood, indentity, etc, but are for example         used to balance, or emphasize statements.<\/p>","href":"bilingualism\/codeswitching\/codeswitch_reasons_for_switching.html#stylistic_function","attributes":{"data-id":"stylistic_function"},"menu":{"hasChildren":false},"tocID":"stylistic_function-d883e917","topics":[]}]});