define({"topics":[{"title":"Standardization (national level)","shortdesc":"<p class=\"shortdesc\">The purpose of standardization is the reduction of number of language variants.         English was standardized during the Middle English and Early modern English         periods.<\/p>","href":"Nationallanguage\/b1t2_standardization_2.html","attributes":{"data-id":"b1t2_standardization"},"menu":{"hasChildren":false},"tocID":"b1t2_standardization-d661e772","topics":[]},{"title":"Haugen Model","shortdesc":"<p class=\"shortdesc\">The Haugen Model is a 5-step breakdown of the standardization procedure.<\/p>","href":"Nationallanguage\/b1t2_standardization_haugen_model_2.html","attributes":{"data-id":"b1t2_standardization_haugen_model"},"menu":{"hasChildren":false},"tocID":"b1t2_standardization_haugen_model-d661e783","topics":[]},{"title":"Standardizing English","shortdesc":"<p class=\"shortdesc\">English was standardized based on the Haugen Model. By (1800) elaboration was         complete.<\/p>","href":"Nationallanguage\/b1t2_standardizing_english_2.html","attributes":{"data-id":"standardizing_english"},"menu":{"hasChildren":false},"tocID":"standardizing_english-d661e794","topics":[]},{"title":"Codification","shortdesc":"<p class=\"shortdesc\">English writing and pronounciation was codified. Writing came first.<\/p>","href":"Nationallanguage\/b1t2_codification_written_2.html","attributes":{"data-id":"b1t2_codification_written"},"menu":{"hasChildren":false},"tocID":"b1t2_codification_written-d661e805","topics":[]}]});